sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
 var rightNow = new GlideDateTime();//here we create the class of glidedatetime

  var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');//we glide the needit task table
  overdueNITasks.addQuery('due_date','<=',rightNow);//give filter for the current time
  overdueNITasks.addQuery('state','<',3);//add query for state is closed
  overdueNITasks.addQuery('assigned_to',current.assigned_to);//to get the current assigned to 

  overdueNITasks.query();//to query the table

  // Print to the Message field on a notification 
  template.print("In addition to this overdue NeedIt Task, you have " + (overdueNITasks.getRowCount() - 1) + " more overdue NeedIt Tasks.");//to print the records and cout the numbers
})(current, template, email, email_action, event);
